//JavaScript
if (typeof(this.datasource.devicename)==="undefined") {
    jQuery("#selectedDevice")[0].innerHTML="Please select from the menu."
    CB_PORTAL.toggleFlyout();
}
else {
    jQuery("#selectedDevice")[0].innerHTML=this.datasource.devicename;
    jQuery("#device_avatar")[0].src=this.datasource.image;
    CB_PORTAL.toggleFlyout();
}

//JavaScript
iconName = "heart";
iconElement = "#displayIcon_heart"
// iterate over each element in the array
icons = datasources.ui_icons.latestData();
for (var i = 0; i < icons.length; i++) {
  // look for the entry with a matching `code` value
  if (icons[i].data.icon_name == iconName) {
    jQuery(iconElement)[0].innerHTML = icons[i].data.icon_data
    // we found it
    // obj[i].name is the matched result
  }
}
jQuery("#displayVal.heart")[0].innerHTML = JSON.parse(this.datasource).heartrate;

